import React,{useState,useEffect} from "react";
import { countriesData } from "../api/holidayApi";
import { CountriesList } from "../types/Holiday";
interface HolidayFormProps {
    onSearch: () => void;
  }
const HolidayForm:React.FC<HolidayFormProps> = ({onSearch}) =>{
    const [CountriesList, setCountryList] = useState<[CountriesList] | null>(null);


const handleSubmit =(e: React.FormEvent<HTMLInputElement>) : void =>{
    onSearch();
}


useEffect( () =>{
    const fetchData = async () => {
        try {
          const data = await countriesData();
          setCountryList(data);
        } catch (err) {
          console.log(err);
        }
      };
  
      fetchData();
    }, []); // Empty dependency array ensures the effect runs once, on component mount
   


    return(
        <>
        <form onSubmit={(e) => {}}>
            <select>
                {CountriesList.map((ele) =>{
                    return(<option> AUS</option>)
                })}
                
                <option> India</option>
            </select>
            <button> Fetch</button>
        </form>
        </>
    )
}
export default HolidayForm;
