import React, { useState } from "react";
import HolidayForm from "./components/HolidayForm";
import HolidayList from "./components/HolidayList";
import { Holiday } from "./types/Holiday";
import { fetchHolidays } from "./api/holidayApi";

const App: React.FC = () => {
  const [holidays, setHolidays] = useState<Holiday | null>(null); // Change to Holiday | null

  const onSearch = async (selectedVal: string) => {
    try {
      const holiday = await fetchHolidays(selectedVal);
      setHolidays(holiday); // No need to wrap in an array
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <>
      <HolidayForm onSearch={onSearch} />
      {holidays && <HolidayList holidays={holidays} />}
    </>
  );
};

export default App;
