    //   setUser(null);
    console.log(error);

holidays
    
    }
}

    return(
        <div>
            <h2> Public Holiday Tracker</h2>
        <HolidayForm onSearch={onSearch} />
        {holidays && <HolidayList holidays={holidays} />}
        
        </div>
    )
}
export default App
